						["subeventPrefix"] = "SPELL",
						["customVariables"] = "{\n    \n    expirationTime = true,\n    duration = true,\n    value = true,\n    total = true,\n    stacks = true,\n    waiting = {\n        display = \"Is waiting for Spells\",\n        type = \"bool\",\n        test = function(state, needle)\n            return (aura_env.started and not aura_env.channeling) == (needle == 1)\n        end,\n        \n        \n    },\n    \n    desaturateChannel= {\n        \n        display = \"[CO]Desaturate no Channel\",\n        type = \"bool\",\n        test = function(state, needle)\n            return aura_env.desaturateChannel == (needle == 1)\n            \n        end,\n        \n        \n    }\n    \n    \n}",
